import React, { Suspense } from "react";
import { Canvas } from "@react-three/fiber";
import { Sphere, Float } from "@react-three/drei";
import { motion } from "framer-motion";

const COLORS = ["#00d9ff", "#ff6b9d", "#ffd700", "#00ff88", "#9d4edd"];

function GalleryImageOrb({ position, index }) {
  return (
    <Float floatIntensity={0.5} speed={1.5}>
      <Sphere position={position} args={[0.6, 32, 32]}>
        <meshStandardMaterial 
          color={COLORS[index % COLORS.length]} 
          emissive={COLORS[index % COLORS.length]}
          emissiveIntensity={0.5}
        />
      </Sphere>
    </Float>
  );
}

function Gallery3DContent() {
  const positions = [
    [0, 0, 0],
    [-2.5, 1.5, -1.5],
    [2.5, 1.5, -1.5],
    [-1.5, -1.5, -2],
    [1.5, -1.5, -2],
  ];

  return (
    <>
      {positions.map((pos, i) => (
        <GalleryImageOrb key={i} position={pos} index={i} />
      ))}
    </>
  );
}

export default function GallerySection() {
  return (
    <section className="gallery-section">
      <motion.h2
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        Gallery & Moments
      </motion.h2>

      <div className="gallery-canvas">
        <Canvas camera={{ position: [0, 0, 8], fov: 45 }}>
          <color attach="background" args={["#0a0e1a"]} />
          <ambientLight intensity={0.6} />
          <pointLight position={[6, 6, 6]} intensity={0.9} color="#00d9ff" />
          <Suspense fallback={null}>
            <Gallery3DContent />
          </Suspense>
        </Canvas>
      </div>

      <p className="gallery-subtitle">5 Interactive Floating Orbs</p>
    </section>
  );
}
