import React, { useState } from "react";
import { motion } from "framer-motion";

export default function ContactSection() {
  const handleEmailClick = () => {
    window.location.href = "mailto:sugamsaproo97@gmail.com";
  };

  const handleResumeDownload = () => {
    // Create a link to download resume from public folder
    const link = document.createElement("a");
    link.href = "/assets/Sugam-Saproo.pdf"; // Make sure file is in public/assets/
    link.download = "Sugam-Saproo-Resume.pdf";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <section className="contact-section">
      <div className="floating-bg">
        <div className="float-item float-1"></div>
        <div className="float-item float-2"></div>
        <div className="float-item float-3"></div>
      </div>

      <motion.div
        className="contact-container"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h2>Let's Create Something Amazing</h2>
        
        <div className="contact-actions">
          <motion.button
            className="contact-btn contact-btn-primary"
            onClick={handleEmailClick}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            ‚úâÔ∏è Contact Me
          </motion.button>

          <motion.button
            className="contact-btn contact-btn-secondary"
            onClick={handleResumeDownload}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            üì• Download Resume
          </motion.button>
        </div>

        <div className="contact-info">
          <motion.div
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            <p>üìß <strong>Email:</strong> sugamsaproo97@gmail.com</p>
            <p>üìç <strong>Location:</strong> Dublin, Ireland</p>
            <p>üíº <strong>LinkedIn:</strong> /in/sugam-saproo</p>
            <p>üîó <strong>GitHub:</strong> github.com/sugamsaproo</p>
          </motion.div>
        </div>
      </motion.div>
    </section>
  );
}
