import React, { Suspense, useState, useEffect } from "react";
import { Canvas } from "@react-three/fiber";
import { Sphere, Float } from "@react-three/drei";
import { motion } from "framer-motion";
import * as THREE from "three";

function GalleryImageOrb({ position, imagePath }) {
  const [texture, setTexture] = useState(null);

  useEffect(() => {
    // Load texture outside of hook
    const textureLoader = new THREE.TextureLoader();
    textureLoader.load(imagePath, (loadedTexture) => {
      setTexture(loadedTexture);
    });
  }, [imagePath]);

  return (
    <Float floatIntensity={0.6} speed={2}>
      <Sphere position={position} args={[0.8, 32, 32]}>
        {texture ? (
          <meshStandardMaterial map={texture} />
        ) : (
          <meshStandardMaterial color="#333" />
        )}
      </Sphere>
    </Float>
  );
}

function Gallery3DContent() {
  const images = [
    "/assets/sugam_lab.jpg",
    "/assets/placeholder1.jpg",
    "/assets/placeholder2.jpg",
    "/assets/placeholder3.jpg",
    "/assets/placeholder4.jpg",
  ];

  const positions = [
    [0, 0, 0],
    [-3, 2, -2],
    [3, 2, -2],
    [-2, -2, -3],
    [2, -2, -3],
  ];

  return (
    <>
      {images.map((imagePath, i) => (
        <GalleryImageOrb key={i} position={positions[i]} imagePath={imagePath} />
      ))}
    </>
  );
}

export default function GallerySection() {
  return (
    <section className="gallery-section">
      <motion.h2
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        Gallery & Moments
      </motion.h2>

      <div className="gallery-canvas">
        <Canvas camera={{ position: [0, 0, 10], fov: 50 }}>
          <color attach="background" args={["#0a0e1a"]} />
          <ambientLight intensity={0.7} />
          <pointLight position={[10, 10, 10]} intensity={1} color="#00d9ff" />
          <Suspense fallback={null}>
            <Gallery3DContent />
          </Suspense>
        </Canvas>
      </div>

      <p className="gallery-subtitle">Click any image to expand</p>
    </section>
  );
}
