import React, { useState } from "react";
import { motion } from "framer-motion";

export default function ContactSection() {
  const [formData, setFormData] = useState({ name: "", email: "", message: "" });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted:", formData);
    // Add email integration here (e.g., EmailJS, Formspree, etc.)
  };

  return (
    <section className="contact-section">
      <div className="floating-bg">
        <div className="float-item float-1"></div>
        <div className="float-item float-2"></div>
        <div className="float-item float-3"></div>
      </div>

      <motion.div
        className="contact-container"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h2>Let's Create Something Amazing</h2>
        <form onSubmit={handleSubmit} className="contact-form">
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Your Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <textarea
            name="message"
            placeholder="Tell me about your project..."
            rows="5"
            value={formData.message}
            onChange={handleChange}
            required
          />
          <button type="submit" className="btn btn-primary">
            Send Message
          </button>
        </form>

        <div className="contact-info">
          <p>üìß sugamsaproo97@gmail.com</p>
          <p>üìç Dublin, Ireland</p>
          <p>üíº LinkedIn | GitHub | Portfolio</p>
        </div>
      </motion.div>
    </section>
  );
}
