import React, { Suspense } from "react";
import { Canvas, useFrame, useThree } from "@react-three/fiber";
import { ScrollControls, Scroll, useScroll, Float, Billboard, Html } from "@react-three/drei";
import { motion } from "framer-motion";
import * as THREE from "three";

function HelixGeometry({ length = 1.5, radius = 0.5, turns = 2 }) {
  const points = [];
  for (let i = 0; i <= 1; i += 1 / (length * 30)) {
    const angle = i * Math.PI * 2 * turns;
    const y = (i - 0.5) * length;
    const x = Math.sin(angle) * radius;
    const z = Math.cos(angle) * radius;
    points.push(new THREE.Vector3(x, y, z));
  }
  const curve = new THREE.CatmullRomCurve3(points);

  return (
    <mesh>
      <tubeGeometry args={[curve, 200, 0.04, 10]} />
      <meshStandardMaterial color="#00d9ff" emissive="#00d9ff" emissiveIntensity={0.4} />
    </mesh>
  );
}

function TimelineCards() {
  const cards = [
    { t: 0.12, title: "2018 - 2021", role: "B.Tech", org: "DCR", desc: "Honors" },
    { t: 0.25, title: "2021 - 22", role: "Lab Asst", org: "Medinnbelle", desc: "QA" },
    { t: 0.38, title: "2022 - 24", role: "Analyst", org: "Suez", desc: "Data" },
    { t: 0.55, title: "2024 - Now", role: "ERP", org: "Singhania", desc: "Dashboards" },
    { t: 0.72, title: "2025 - Now", role: "MSc PM", org: "UCD", desc: "Strategy" },
  ];

  return (
    <>
      {cards.map((card, i) => {
        const angle = card.t * Math.PI * 2 * 2;
        const y = (card.t - 0.5) * 1.5;
        const x = Math.sin(angle) * 1.2;
        const z = Math.cos(angle) * 1.2;

        return (
          <Billboard key={i} position={[x, y, z]}>
            <Float floatIntensity={0.25} speed={0.8}>
              <Html distanceFactor={10} scale={0.6}>
                <motion.div
                  className="timeline-card"
                  initial={{ opacity: 0, scale: 0.7 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ delay: i * 0.08 }}
                  whileHover={{ scale: 1.05 }}
                >
                  <div className="card-period">{card.title}</div>
                  <div className="card-role">{card.role}</div>
                  <div className="card-org">{card.org}</div>
                  <div className="card-desc">{card.desc}</div>
                </motion.div>
              </Html>
            </Float>
          </Billboard>
        );
      })}
    </>
  );
}

function CameraController() {
  const scroll = useScroll();
  const { camera } = useThree();
  useFrame(() => {
    const t = scroll.offset;
    const angle = t * Math.PI * 2 * 2;
    const y = (t - 0.5) * 1.5;
    const r = 2.5;
    camera.position.set(Math.sin(angle) * r, y, Math.cos(angle) * r);
    camera.lookAt(0, y, 0);
  });
  return null;
}

function HelixScene() {
  return (
    <>
      <HelixGeometry />
      <TimelineCards />
      <CameraController />
    </>
  );
}

export default function TimelineHelix() {
  return (
    <section className="timeline-section">
      <Canvas camera={{ position: [0, 0, 3], fov: 50 }}>
        <color attach="background" args={["#0a0e1a"]} />
        <ambientLight intensity={0.5} />
        <pointLight position={[2, 2, 2]} intensity={0.7} color="#00d9ff" />
        <ScrollControls pages={0.8} damping={0.2}>
          <Scroll>
            <Suspense fallback={null}>
              <HelixScene />
            </Suspense>
          </Scroll>
          <CameraController />
        </ScrollControls>
      </Canvas>
    </section>
  );
}
