import React, { useRef, useLayoutEffect } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { ScrollControls, Scroll, useScroll, useTexture, Float, Billboard, Html } from '@react-three/drei';
import * as THREE from 'three';

function Helix({ length = 8, radius = 2, turns = 3 }) {
  const points = [];
  const rungStep = 1 / (length * 30);
  // DNA helix path points:
  for (let i = 0; i <= 1; i += rungStep) {
    const angle = i * Math.PI * 2 * turns;
    const y = (i - 0.5) * length;
    const x = Math.sin(angle) * radius;
    const z = Math.cos(angle) * radius;
    points.push(new THREE.Vector3(x, y, z));
  }
  const curve = new THREE.CatmullRomCurve3(points);

  return (
    <mesh>
      <tubeGeometry args={[curve, 600, 0.07, 24]} />
      <meshStandardMaterial color="#00f5ff" emissive="#00f5ff" emissiveIntensity={0.14} />
    </mesh>
  );
}

// Content cards positioned along the helix (NFT/Polaroid style)
function TimelineCards({ sections, length, turns, radius }) {
  return (
    <>
      {sections.map((section, i) => {
        const t = section.t;
        const angle = t * Math.PI * 2 * turns;
        const y = (t - 0.5) * length;
        const x = Math.sin(angle) * (radius + 1.2);
        const z = Math.cos(angle) * (radius + 1.2);

        return (
          <Billboard key={i} position={[x, y, z]}>
            <Float
              speed={section.photo ? 2 : 1}
              floatIntensity={section.photo ? 1 : 0.3}
              rotationIntensity={section.photo ? 0.25 : 0.1}
            >
              <group>
                {/* Card content/photo: photo gets special style */}
                {section.photo ? (
                  <mesh>
                    <planeGeometry args={[2.1, 3]} />
                    <meshStandardMaterial
                      color="#fff"
                      emissive="#ffd700"
                      emissiveIntensity={0.3}
                      map={section.img}
                    />
                    <Html distanceFactor={6}>
                      <div style={{
                        background: "rgba(0,0,0,0.7)",
                        color: "#fff",
                        borderRadius: "12px",
                        padding: "0.4em 1.5em",
                        fontWeight: "bold",
                        marginTop: "1.7em",
                        boxShadow: "0 6px 22px #00f5ff77, 0 0 2px #9d4edd"
                      }}>
                        {section.title}
                      </div>
                    </Html>
                  </mesh>
                ) : (
                  <Html distanceFactor={10}>
                    <div
                      style={{
                        background: "rgba(20,24,44,0.98)",
                        boxShadow: "0 12px 36px #00f5ffd9, 0 0 0 #ffd700",
                        border: "2.5px solid #ffd700",
                        borderRadius: "20px",
                        padding: "1.3em 1.9em",
                        width: 265,
                        color: "#fff",
                        fontSize: 17,
                        transition: "box-shadow 400ms",
                        textAlign: "left",
                        fontFamily: "'Inter', 'sans-serif'"
                      }}
                    >
                      <b>{section.title}</b>
                      <div style={{ color: "#00f5ff", margin: "0.5em 0" }}>{section.sub}</div>
                      <div style={{ color: "#ffd700" }}>{section.period}</div>
                      <div style={{ color: "#aaa" }}>{section.desc}</div>
                    </div>
                  </Html>
                )}
                {/* DNA glow effect at the base of photo */}
                <mesh position={[0, section.photo ? -1.5 : -1.1, 0]}>
                  <sphereGeometry args={[0.25, 12, 12]} />
                  <meshStandardMaterial color="#ffd700" emissive="#ffd700" emissiveIntensity={0.32} transparent opacity={.1} />
                </mesh>
              </group>
            </Float>
          </Billboard>
        );
      })}
    </>
  );
}

function CameraControlsHelix({ numSections }) {
  // dynamic scroll camera along the helix
  const scroll = useScroll();
  const { camera } = useThree();
  useFrame(() => {
    // scroll.offset goes from 0 to 1 as you scroll
    const t = scroll.offset;
    // t: [0,1], map along helix vertical length
    const angle = t * Math.PI * 2 * 3;
    const y = (t - 0.5) * 8;
    const x = Math.sin(angle) * 5.3;
    const z = Math.cos(angle) * 5.3;
    camera.position.set(x, y, z);
    camera.lookAt(0, y, 0); // always look at midpoint on helix for immersion
  });
  return null;
}

// PNG/JPG loader for images:
function usePolaroidTexture(url) {
  const tex = useTexture(url);
  return tex;
}

export default function DNAHelixScene() {
  // These sections appear along the helix and tell the story
  const photoTexture = usePolaroidTexture('/assets/sugam_lab.jpg');

  const sections = [
    {
      t: 0.1,
      title: "2018 - 2021",
      sub: "B.Tech Biotechnology",
      period: "DCR University",
      desc: "Graduated with honors in Biotechnology. Gained strong foundation in microbiology.",
    },
    {
      t: 0.25,
      title: "2021 - 2022",
      sub: "Lab Assistant",
      period: "Medinnbelle Herbalcare Pvt. Ltd.",
      desc: "Maintained lab ops and quality assurance. Fast learner and mentor.",
    },
    {
      t: 0.39,
      title: "2022 - 2024",
      sub: "Lab Analyst",
      period: "Suez India Pvt Ltd",
      desc: "Ran analytics, data integrity, dashboards across environmental projects.",
    },
    {
      t: 0.55,
      title: "2024 - Present",
      sub: "ERP Executive",
      period: "Singhania University",
      desc: "ERP System Implementation, Dashboard Creation & Data Visualization.",
    },
    {
      t: 0.68,
      title: "2025 - Present",
      sub: "MSc Business Analytics",
      period: "UCD Michael Smurfit Graduate Business School",
      desc: "Immersed in project management, strategy, and analytics at a world-class institution.",
    },
    {
      t: 0.48,
      title: "Sugam in Action!",
      photo: true,
      img: photoTexture,
      desc: "",
    },
  ];

  return (
    <div style={{ height: '100vh', width: '100vw', background: "#0C1221" }}>
      <Canvas linear camera={{ position: [0, 0, 8], fov: 60 }}>
        <color attach="background" args={['#0C1221']} />
        <ambientLight intensity={0.66} />
        <spotLight position={[0, 10, 10]} angle={1.3} penumbra={1} intensity={1.2} castShadow />
        <ScrollControls pages={1.3} damping={0.35} horizontal={false}>
          <Scroll>
            <Helix />
            <TimelineCards sections={sections} length={8} turns={3} radius={2} />
          </Scroll>
          <CameraControlsHelix numSections={sections.length} />
        </ScrollControls>
      </Canvas>
      <div
        style={{
          position: 'absolute',
          top: 32,
          left: 0,
          width: '100%',
          textAlign: 'center',
          color: 'white',
          textShadow: '0 4px 24px #0ff',
          zIndex: 99,
          fontSize: 28,
          fontWeight: 900,
          letterSpacing: '.02em'
        }}>
        Sugam Saproo: <span style={{ color: '#00f5ff' }}>Inside The DNA of a Project Manager</span>
      </div>
    </div>
  );
}
