import React, { Suspense } from "react";
import { Canvas, useFrame, useThree } from "@react-three/fiber";
import { ScrollControls, Scroll, useScroll, Float, Billboard, Html } from "@react-three/drei";
import { motion } from "framer-motion";
import * as THREE from "three";

function HelixGeometry({ length = 6, radius = 1.5, turns = 2.5 }) {
  const points = [];
  for (let i = 0; i <= 1; i += 1 / (length * 40)) {
    const angle = i * Math.PI * 2 * turns;
    const y = (i - 0.5) * length;
    const x = Math.sin(angle) * radius;
    const z = Math.cos(angle) * radius;
    points.push(new THREE.Vector3(x, y, z));
  }
  const curve = new THREE.CatmullRomCurve3(points);

  return (
    <mesh>
      <tubeGeometry args={[curve, 400, 0.08, 16]} />
      <meshStandardMaterial color="#00d9ff" emissive="#00d9ff" emissiveIntensity={0.3} />
    </mesh>
  );
}

function TimelineCards() {
  const cards = [
    { t: 0.12, title: "2018 - 2021", role: "B.Tech Biotechnology", org: "DCR University", desc: "Graduated with honors" },
    { t: 0.25, title: "2021 - 2022", role: "Lab Assistant", org: "Medinnbelle Herbalcare", desc: "Quality assurance" },
    { t: 0.38, title: "2022 - 2024", role: "Lab Analyst", org: "Suez India Pvt Ltd", desc: "Data analytics" },
    { t: 0.55, title: "2024 - Present", role: "ERP Executive", org: "Singhania University", desc: "ERP & dashboards" },
    { t: 0.72, title: "2025 - Present", role: "MSc Project Management", org: "UCD Michael Smurfit", desc: "Strategic leadership" },
  ];

  return (
    <>
      {cards.map((card, i) => {
        const angle = card.t * Math.PI * 2 * 2.5;
        const y = (card.t - 0.5) * 6;
        const x = Math.sin(angle) * 3.2;
        const z = Math.cos(angle) * 3.2;

        return (
          <Billboard key={i} position={[x, y, z]}>
            <Float floatIntensity={0.4} speed={1.5}>
              <Html distanceFactor={15}>
                <motion.div
                  className="timeline-card"
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ delay: i * 0.15 }}
                  whileHover={{ scale: 1.08, boxShadow: "0 0 40px #00d9ff" }}
                >
                  <div className="card-period">{card.title}</div>
                  <div className="card-role">{card.role}</div>
                  <div className="card-org">{card.org}</div>
                  <div className="card-desc">{card.desc}</div>
                </motion.div>
              </Html>
            </Float>
          </Billboard>
        );
      })}
    </>
  );
}

function CameraController() {
  const scroll = useScroll();
  const { camera } = useThree();
  useFrame(() => {
    const t = scroll.offset;
    const angle = t * Math.PI * 2 * 2.5;
    const y = (t - 0.5) * 6;
    const r = 6;
    camera.position.set(Math.sin(angle) * r, y, Math.cos(angle) * r);
    camera.lookAt(0, y, 0);
  });
  return null;
}

function HelixScene() {
  return (
    <>
      <HelixGeometry />
      <TimelineCards />
      <CameraController />
    </>
  );
}

export default function TimelineHelix() {
  return (
    <section className="timeline-section">
      <Canvas camera={{ position: [0, 0, 8], fov: 50 }}>
        <color attach="background" args={["#0a0e1a"]} />
        <ambientLight intensity={0.7} />
        <pointLight position={[5, 5, 5]} intensity={1} color="#00d9ff" />
        <pointLight position={[-5, -5, 5]} intensity={0.6} color="#ff6b9d" />
        <ScrollControls pages={1} damping={0.3}>
          <Scroll>
            <Suspense fallback={null}>
              <HelixScene />
            </Suspense>
          </Scroll>
          <CameraController />
        </ScrollControls>
      </Canvas>
    </section>
  );
}
