import React, { Suspense, useState, useEffect } from "react";
import { Canvas } from "@react-three/fiber";
import { Sphere, Float } from "@react-three/drei";
import { motion } from "framer-motion";
import * as THREE from "three";

const COLORS = ["#00d9ff", "#ff6b9d", "#ffd700", "#00ff88", "#9d4edd"];
const IMAGE_PATHS = [
  "/assets/sugam_lab.jpg",
  "/assets/placeholder1.jpg",
  "/assets/placeholder2.jpg",
  "/assets/placeholder3.jpg",
  "/assets/placeholder4.jpg",
];

function GalleryImageOrb({ position, index, imagePath }) {
  const [texture, setTexture] = useState(null);

  useEffect(() => {
    const loader = new THREE.TextureLoader();
    loader.load(
      imagePath,
      (tex) => setTexture(tex),
      undefined,
      () => {
        // Fallback to color if image fails to load
        setTexture(null);
      }
    );
  }, [imagePath]);

  return (
    <Float floatIntensity={0.5} speed={1.4}>
      <Sphere position={position} args={[0.7, 32, 32]}>
        {texture ? (
          <meshStandardMaterial map={texture} />
        ) : (
          <meshStandardMaterial 
            color={COLORS[index % COLORS.length]} 
            emissive={COLORS[index % COLORS.length]}
            emissiveIntensity={0.5}
          />
        )}
      </Sphere>
    </Float>
  );
}

function Gallery3DContent() {
  const positions = [
    [0, 0, 0],
    [-2, 1.2, -1.5],
    [2, 1.2, -1.5],
    [-1.5, -1.5, -2],
    [1.5, -1.5, -2],
  ];

  return (
    <>
      {IMAGE_PATHS.map((path, i) => (
        <GalleryImageOrb key={i} position={positions[i]} index={i} imagePath={path} />
      ))}
    </>
  );
}

export default function GallerySection() {
  return (
    <section className="gallery-section">
      <motion.h2
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        Gallery & Moments
      </motion.h2>

      <div className="gallery-canvas">
        <Canvas camera={{ position: [0, 0, 6], fov: 45 }}>
          <color attach="background" args={["#0a0e1a"]} />
          <ambientLight intensity={0.7} />
          <pointLight position={[5, 5, 5]} intensity={1} color="#00d9ff" />
          <pointLight position={[-5, -5, 5]} intensity={0.7} color="#ff6b9d" />
          <Suspense fallback={null}>
            <Gallery3DContent />
          </Suspense>
        </Canvas>
      </div>

      <p className="gallery-subtitle">âœ¨ 5 Interactive Image Orbs</p>
    </section>
  );
}
