import React from "react";
import { motion } from "framer-motion";

export default function ContactSection() {
  const handleEmailClick = () => {
    window.location.href = "mailto:sugamsaproo97@gmail.com";
  };

  const handleResumeDownload = () => {
    // For React, anything inside public/ is served from root:
    // public/assets/Shivam-Sapru-FlowCV-Resume-20250721.pdf
    const link = document.createElement("a");
    link.href = "/assets/Shivam-Sapru-FlowCV-Resume-20250721.pdf";
    link.download = "Shivam-Sapru-Resume.pdf";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <section className="contact-section">
      <div className="floating-bg">
        <div className="float-item float-1"></div>
        <div className="float-item float-2"></div>
        <div className="float-item float-3"></div>
      </div>

      <motion.div
        className="contact-container"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="contact-header">

          <div className="contact-header-text">
            <h2>Let‚Äôs Create Something Amazing</h2>
            <p className="contact-tagline">
              Open to collaborations in microbiology, project management, and analytics.
            </p>
          </div>
        </div>

        <div className="contact-actions">
          <motion.button
            className="contact-btn contact-btn-primary"
            onClick={handleEmailClick}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            ‚úâÔ∏è Contact Me
          </motion.button>

          <motion.button
            className="contact-btn contact-btn-secondary"
            onClick={handleResumeDownload}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            üì• Download Resume
          </motion.button>
        </div>

        <div className="contact-info">
          <p>üìß <strong>Email:</strong> sugamsaproo97@gmail.com</p>
          <p>üìç <strong>Location:</strong> Dublin, Ireland</p>
          <p>üíº <strong>LinkedIn:</strong> /in/sugam-saproo</p>
          <p>üîó <strong>GitHub:</strong> github.com/sugamsaproo</p>
        </div>
      </motion.div>
    </section>
  );
}
