import React, { Suspense } from "react";
import { Canvas, useFrame, useThree } from "@react-three/fiber";
import { ScrollControls, Scroll, useScroll, Float, Billboard, Html } from "@react-three/drei";
import { motion } from "framer-motion";
import * as THREE from "three";

function DoubleHelixGeometry({ length = 5, radius = 1.2, turns = 3 }) {
  const points1 = [];
  const points2 = [];
  
  for (let i = 0; i <= 1; i += 1 / (length * 35)) {
    const angle = i * Math.PI * 2 * turns;
    const y = (i - 0.5) * length;
    
    // Strand 1
    const x1 = Math.sin(angle) * radius;
    const z1 = Math.cos(angle) * radius;
    points1.push(new THREE.Vector3(x1, y, z1));
    
    // Strand 2 (opposite side)
    const x2 = Math.sin(angle + Math.PI) * radius;
    const z2 = Math.cos(angle + Math.PI) * radius;
    points2.push(new THREE.Vector3(x2, y, z2));
  }
  
  const curve1 = new THREE.CatmullRomCurve3(points1);
  const curve2 = new THREE.CatmullRomCurve3(points2);

  return (
    <group>
      {/* Strand 1 */}
      <mesh>
        <tubeGeometry args={[curve1, 400, 0.08, 16]} />
        <meshStandardMaterial color="#00d9ff" emissive="#00d9ff" emissiveIntensity={0.35} />
      </mesh>
      
      {/* Strand 2 */}
      <mesh>
        <tubeGeometry args={[curve2, 400, 0.08, 16]} />
        <meshStandardMaterial color="#ff6b9d" emissive="#ff6b9d" emissiveIntensity={0.35} />
      </mesh>
    </group>
  );
}

function TimelineCards() {
  const cards = [
    { t: 0.12, title: "2018 - 2021", role: "B.Tech Biotech", org: "DCR University", desc: "Honors" },
    { t: 0.25, title: "2021 - 2022", role: "Lab Assistant", org: "Medinnbelle", desc: "QA Ops" },
    { t: 0.38, title: "2022 - 2024", role: "Lab Analyst", org: "Suez India", desc: "Analytics" },
    { t: 0.55, title: "2024 - Present", role: "ERP Executive", org: "Singhania", desc: "Dashboards" },
    { t: 0.72, title: "2025 - Present", role: "MSc PM", org: "UCD Smurfit", desc: "Strategy" },
  ];

  return (
    <>
      {cards.map((card, i) => {
        const angle = card.t * Math.PI * 2 * 3;
        const y = (card.t - 0.5) * 5;
        const x = Math.sin(angle) * 2.2;
        const z = Math.cos(angle) * 2.2;

        return (
          <Billboard key={i} position={[x, y, z]}>
            <Float floatIntensity={0.35} speed={1.2}>
              <Html distanceFactor={14} scale={0.8}>
                <motion.div
                  className="timeline-card-small"
                  initial={{ opacity: 0, scale: 0.75 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ delay: i * 0.1 }}
                  whileHover={{ scale: 1.08, boxShadow: "0 0 30px #ff6b9d" }}
                >
                  <div className="card-period-small">{card.title}</div>
                  <div className="card-role-small">{card.role}</div>
                  <div className="card-org-small">{card.org}</div>
                </motion.div>
              </Html>
            </Float>
          </Billboard>
        );
      })}
    </>
  );
}

function CameraController() {
  const scroll = useScroll();
  const { camera } = useThree();
  useFrame(() => {
    const t = scroll.offset;
    const angle = t * Math.PI * 2 * 3;
    const y = (t - 0.5) * 5;
    const r = 6;
    camera.position.set(Math.sin(angle) * r, y, Math.cos(angle) * r);
    camera.lookAt(0, y, 0);
  });
  return null;
}

function HelixScene() {
  return (
    <>
      <DoubleHelixGeometry />
      <TimelineCards />
      <CameraController />
    </>
  );
}

export default function TimelineHelix() {
  return (
    <section className="timeline-section">
      <Canvas camera={{ position: [0, 0, 7], fov: 50 }}>
        <color attach="background" args={["#0a0e1a"]} />
        <ambientLight intensity={0.7} />
        <pointLight position={[5, 5, 5]} intensity={1} color="#00d9ff" />
        <pointLight position={[-5, -5, 5]} intensity={0.8} color="#ff6b9d" />
        <ScrollControls pages={1} damping={0.28}>
          <Scroll>
            <Suspense fallback={null}>
              <HelixScene />
            </Suspense>
          </Scroll>
          <CameraController />
        </ScrollControls>
      </Canvas>
    </section>
  );
}
